const Product = ({ img, content, piont, review }) => {
  // Đường dẫn đầy đủ tới ảnh trong public
  const imagePath = process.env.PUBLIC_URL + img;

  return (
    <div className="w-[259px] h-[472px] rounded-sm ml-4">
      {/* Hiển thị ảnh sử dụng đường dẫn đầy đủ */}
      <img className="pt-4 pl-4 pr-4 pb" src={imagePath} alt="Product" />
      <p className="text-[16px] pl-4">{content}</p>
      <p className="text-[24px] font-bold ml-4 my-1 ">{piont}</p>
      <p className="text-[14px] text-[#787885] ml-4">{review}</p>
    </div>
  );
};

export default Product;
