import Product1 from "../../Img/Image.png"
import Product2 from "../../Img/Image2.png"
import Product from "../LessionTwo/ListProduct.js"
import { useState, useEffect } from 'react'
function Index() {
  const TAGS = ["Show all", "Auction", "Buy Now"]
  const MENU = ["Useless", "Condition", "Delivery options"]
  const OPTIONS = ["Related", "worldwide shipping", "under $50", "kitten", "plastic plugs", "pucker shoes", "vintage typewriter"]


  // const ArrayProduct = [
  //   {
  //     img: { src: Product1 },
  //     content: "Vintage Typewriter to post awesome stories about UI design and webdev.",
  //     price: "$49.50",
  //     review: "Eligible for Shipping To Mars or somewhere else"
  //   },
  //   {
  //     img: { src: Product2 },
  //     content: "Lee Pucker design. Leather botinki for handsome designers. Free shipping.",
  //     price: "$13.95",
  //     review: "1258 bids, 359 watchers $5.95 for shipping"
  //   }

  // ]

  const [ArrayProduct, setArrayProduct] = useState([]);
  useEffect(() => {
    fetch('http://localhost:5000/api/products')
      .then(res => res.json())
      .then(res => setArrayProduct(res))
      .catch(err => console.error(err))
  }, [])

  return (
    <div className="">
      <div className=" my-8 ">
        {MENU.map((menu, index) => (
          <a key={index} className="w-[136px] h-[36px] items-center justify-center text-[14px] inline-flex rounded-lg border border-black-200 ml-4" id="menu">{menu}
            <svg className="ml-2 mt-1" xmlns="http://www.w3.org/2000/svg" width="10" height="5" viewBox="0 0 10 5" fill="none">
              <path d="M0 0L5 5L10 0L0 0Z" fill="#787885" />
            </svg> </a>
        ))}
        <div className="ml-[234px] inline-flex">
          {TAGS.map(tags => (
            <a className="ml-1 inline-flex w-[85px] h-[36px] items-center justify-center bg-[#EBF2FF] rounded-md" id="tags">{tags}</a>
          ))}
        </div>

        <div className="ml-40 inline-block px-5 py-1 bg-[#EBF2FF]">
          <svg className="inline-block pb-1" width="17" height="14" viewBox="0 0 17 14" fill="none" xmlns="http://www.w3.org/2000/svg">
            <g id="view-list (1)">
              <path id="Vector" d="M5 0V4H17V0H5ZM5 14H17V10H5V14ZM5 9H17V5H5V9ZM0 4H4V0H0V4ZM0 14H4V10H0V14ZM0 9H4V5H0V9Z" fill="#787885" />
            </g>
          </svg>

        </div>
        <div className="inline-block px-5 py-1 bg-white box-shadow">
          <svg className="inline-block pb-1 " width="18" height="18" viewBox="0 0 18 18" fill="none" xmlns="http://www.w3.org/2000/svg">
            <g id="view-grid (1)">
              <path id="Vector" d="M0 8H8V0H0V8ZM0 18H8V10H0V18ZM10 18H18V10H10V18ZM10 0V8H18V0" fill="#2979FF" />
            </g>
          </svg>
        </div>
      </div>

      <div className="ml-2 inline-flex">
        {OPTIONS.map(option => (
          <a className="ml-2 w-[160px] h-[32px] rounded-2xl bg-[#EDEDF0] text-center flex items-center justify-center" id="option">
            {option}
          </a>
        ))}
      </div>

      <div className="grid grid-cols-4 gap-1">
        {ArrayProduct.map((ars, index) => (
          <Product key={index} img={ars.img} content={ars.content} price={ars.price} review={ars.review} />
        ))}
      </div>

    </div>
  )
}
export default Index
