import { useState } from "react";

const MenuItem = ({ data }) => {
  const [activeMenu, setActiveMenu] = useState(false);
  return (
    <a key={data.id} className="w-[136px] relative h-[36px] items-center justify-center inline-flex rounded-lg border border-black-200 ml-4" id="menu">
      <button
        onClick={() => setActiveMenu(!activeMenu)}
        className="text-[14px]">
        {data.name}
      </button>
      <svg className="ml-2 mt-1" xmlns="http://www.w3.org/2000/svg" width="10" height="5" viewBox="0 0 10 5" fill="none">
        <path d="M0 0L5 5L10 0L0 0Z" fill="#787885" />
      </svg>
      {/* {activeMenu === menu.id && (
        <ul className={`absolute top-9 left-0 w-full h-[100px] bg-red-200`}>
          <li>1</li>
          <li>2</li>
        </ul>
      )} */}
    </a>
  );
};
export default MenuItem;
